import View from "./view";
class CartView extends View {
  constructor() {
    super();
    this._parentElement = document.getElementsByClassName(
      "cart-icon-container"
    )[0];
  }
  generateMarkup() {
    let cartCnt =
      this._data.length > 1
        ? this._data.reduce((e, acc) => e + acc.quantity, 0)
        : this._data[0]?.quantity ?? 0;
    return [
      `<svg xmlns="http://www.w3.org/2000/svg" class="cart-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor"
                    stroke-width="2">
                    <path stroke-linecap="round" stroke-linejoin="round"
                        d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
      <p class="cart-quantity">${cartCnt}</p>`,
    ];
  }
  cartBtnHandlerRedner(handler) {
    document
      .getElementsByClassName("cart-icon-container")[0]
      .addEventListener("click", handler);
  }
}
export default CartView;
